var lan = ['1', '2', '3', '4', '5', '6', '7', '8', '9', '0', 'b', 'c', 'd', 'f', 'g', 'h', 'j',
           'k', 'l', 'm', 'n', 'p', 'q', 'r', 's', 't', 'v', 'w', 'x', 'y', 'z'],
    revLan = {
      '1': 0, '2': 1, '3': 2, '4': 3, '5': 4, '6': 5, '7': 6, '8': 7, '9': 8, '0': 9,
      'b': 10, 'c': 11, 'd': 12, 'f': 13, 'g': 14, 'h': 15, 'j': 16, 'k': 17, 'l': 18,
      'm': 19, 'n': 20, 'p': 21, 'q': 22, 'r': 23, 's': 24, 't': 25, 'v': 26, 'w': 27,
      'x': 28, 'y': 29, 'z': 30
    }

exports.urlToLinkID = function(url) {
  var id = 0,
      cnt = lan.length;

  for (var idx = url.length - 1; idx >= 0; idx--) {
    id *= cnt;
    id += revLan[url[idx]];
  }

  return id;
};

exports.linkIDToURL = function(linkID) {
  var cnt = lan.length,
      tmp = 0,
      urlBuild = [];

  while (linkID > 0) {
    tmp = linkID % cnt;
    urlBuild.push(lan[tmp]);
    linkID -= tmp;
    linkID /= cnt;
  }

  return urlBuild.join('');
};